// TODO -- handle global plugins
module.exports = setplugins

// accepts single plugin assignments (array)
// accepts multiple plugin assignments (array of arrays)
function setplugins(schema, plugins) {
  plugins.forEach(e => {
  	if (e[1]) {
      schema.plugin(e[0], e[1])
    }
    else {
      schema.plugin(e[0])
    }
  })

  return schema
}
